import React, { Component } from "react";
// import { v4 as uuidv4 } from 'uuid';
import CompanyList from './CompanyList';

// component needs to provide a search form, then display any search results after input is sumbitted.
// results list should save information to state but only show current search results
// on submit form information needs to be fetched

const API_KEY = 'CZMRTN1SEP2UQCBY';
// const BASE_URL =
//   'https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=' +
//   `api-key=${NYT_API_KEY}&query=`;

export default class CompanySearchForm extends Component {
    state = {
          symbol: "",
          searchResultsList: []
    }

    handleChange = event => {
        this.setState({
            symbol: event.target.value
        })
    }

    handleSubmit = event => {
        event.preventDefault()

        fetch(`https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${this.state.symbol}&apikey=${API_KEY}`)
            .then((res) => res.json())
            // .then((data) => console.log(data));
            .then(data => {
                const companies = Object(data.bestMatches);
                this.setState({ 
                    searchResultsList: companies
            })
        })
    }

    renderCompanyList() {
        return (
            <>
                {this.state.searchResultsList.map((company) => (
                    <CompanyList 
                        companySymbol={company["1. symbol"]} 
                        companyName={company["2. name"]}
                    />
                ))}
            </>
        )
    }

    render() {
        return (
            <>
                <form onSubmit={this.handleSubmit}>
                    <input 
                        type="text" 
                        name="symbol" 
                        value={this.state.symbol} 
                        onChange={this.handleChange}
                    />
                    <button type="submit">Search</button>
                </form>
                <div>
                    {this.renderCompanyList()}
                </div>
                {/* <div>
                    {this.state.searchResultsList.map((company) => (
                <ul key={company.id}> 
                    symbol: {company["1. symbol"]} <br/>
                    Company Name: {company["2. name"]} <br/>
                    Industry Type: {company["3. type"]} <br/>
                    Region: {company["4. region"]} <br/>
                    Currency: {company["8. currency"]} <br/>
                </ul>
                    ))}
                </div> */}
            </>
        );
    };
}
