import {connect} from 'react-redux'

const setCompany = (companySymbol) => ({
    type: "SET_COMPANY",
    payload: {companySymbol: companySymbol}
})

const getCompany = () => {
    return (dispatch) => {
        const apiKey = process.env.API_KEY;
        fetch(`https://www.alphavantage.co/query?function=OVERVIEW&symbol=${this.props.currentCompanySymbol["companySymbol"]}&apikey=${apiKey}`)
            .then((res) => res.json())
            .then(data =>  
                {
                const company = Object(data);
                console.log(Object(data))

                dispatch({ 
                    type: "GET_COMPANY",
                    payload: { 
                        company
                    },
                })
                // store.currentCompanySymbol += company["Symbol"]
            })
    }
}

export default connect() (setCompany, getCompany)
