import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from "react-router-dom";
import {connect} from 'react-redux'
import {setCompany} from '../redux/actionCreator'
function CompanyCard(props) {
    const {symbol, name, type, region, currency, id} = props.company
    return ( 
        <>
            <Card id={id} style={{ width: '30rem', margin: '1rem auto' }} >
                <Card.Header as="h5">Company Symbol: {symbol}</Card.Header>
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                    <Card.Text>
                        Industry Type: {type} <br/>
                        Region: {region} <br/>
                        Currency: {currency} <br/>
                    </Card.Text>
                    <Link to={`/company-overview/${id}`}>
                        <Button variant="dark" onClick={() => props.setCompany(props.company)}> Explore </Button>{' '}
                    </Link>
                </Card.Body>
            </Card>
        </>
    )
}



export default connect(null, {setCompany})(CompanyCard)
